dt-example.js:
var table = $('#example').DataTable({
	fixedColumns: true,
	paging: false,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});

$('#increaseLeft').on('click', function () {
	let fc = table.fixedColumns();

	fc.left(fc.left() + 1);

	$('#click-output').prepend('<div>Set fixed columns left: ' + fc.left() + '</div>');
});

$('#decreaseLeft').on('click', function () {
	let fc = table.fixedColumns();

	fc.left(fc.left() - 1);

	$('#click-output').prepend('<div>Set fixed columns left: ' + fc.left() + '</div>');
});

$('#increaseRight').on('click', function () {
	let fc = table.fixedColumns();

	fc.right(fc.right() + 1);

	$('#click-output').prepend('<div>Set fixed columns right: ' + fc.right() + '</div>');
});

$('#decreaseRight').on('click', function () {
	let fc = table.fixedColumns();

	fc.right(fc.right() - 1);

	$('#click-output').prepend('<div>Set fixed columns right: ' + fc.right() + '</div>');
});

dt-example.js-vanilla:
function addOutput(message) {
	let el = document.querySelector('#click-output');
	let div = document.createElement('div');

	div.textContent = message;
	el.prepend(div);
}

let table = new DataTable('#example', {
	fixedColumns: true,
	paging: false,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});

document.querySelector('#increaseLeft').addEventListener('click', function () {
	let fc = table.fixedColumns();

	fc.left(fc.left() + 1);

	addOutput('Set fixed columns left: ' + fc.left());
});

document.querySelector('#decreaseLeft').addEventListener('click', function () {
	let fc = table.fixedColumns();

	fc.left(fc.left() - 1);

	addOutput('Set fixed columns left: ' + fc.left());
});

document.querySelector('#increaseRight').addEventListener('click', function () {
	let fc = table.fixedColumns();

	fc.right(fc.right() + 1);

	addOutput('Set fixed columns right: ' + fc.right());
});

document.querySelector('#decreaseRight').addEventListener('click', function () {
	let fc = table.fixedColumns();

	fc.right(fc.right() - 1);

	addOutput('Set fixed columns right: ' + fc.right());
});
