dt-example.css:
/* Ensure that the demo table scrolls */
th, td { white-space: nowrap; }
div.dataTables_wrapper {
	width: 800px;
	margin: 0 auto;
}

dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		dom: 'Blfrtip',
		scrollY: 300,
		scrollX: true,
		scrollCollapse: true,
		paging: false,
		buttons: [{
			extend: 'fixedColumns',
			text: 'FixedColumns',
			config: {
				left: 1,
				right: 0
			}
		}]
	} );
} );
