dt-example.css:
/* Ensure that the demo table scrolls */
th, td { white-space: nowrap; }
div.dataTables_wrapper {
	width: 800px;
	margin: 0 auto;
}

dt-example.js:
$('#example').DataTable({
	fixedColumns: {
		left: 0,
		right: 1
	},
	paging: false,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});

dt-example.js-vanilla:
new DataTable('#example', {
	fixedColumns: {
		left: 0,
		right: 1
	},
	paging: false,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});
