dt-example.js:
$('#example').DataTable({
	fixedColumns: true,
	fixedHeader: true,
	paging: false,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});

dt-example.js-vanilla:
new DataTable('#example', {
	fixedColumns: true,
	fixedHeader: true,
	paging: false,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});
