dt-example.css:
/* Ensure that the demo table scrolls */
th, td { white-space: nowrap; }
div.dataTables_wrapper {
	width: 600px;
	margin: 0 auto;
}

dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		scrollY:        300,
		scrollX:        true,
		scrollCollapse: true,
		paging:         false,
		rowReorder:     true,
		fixedColumns: 	true
	} );
} );
