dt-example.css:
/* Ensure that the demo table scrolls */
th, td { white-space: nowrap; }
div.dataTables_wrapper {
	width: 600px;
	margin: 0 auto;
}

dt-example.js:
$('#example').DataTable({
	fixedColumns: true,
	paging: false,
	rowReorder: true,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});

dt-example.js-vanilla:
new DataTable('#example', {
	fixedColumns: true,
	paging: false,
	rowReorder: true,
	scrollCollapse: true,
	scrollX: true,
	scrollY: 300
});
