div.dataTables_wrapper {
	max-width: 800px;
	margin: 0 auto;
}

dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		scrollY:        "300px",
		scrollX:        true,
		scrollCollapse: true,
		paging:         false,
		fixedColumns:   true
	} );
} );
